import React, { useContext } from 'react'
import spain from './imagenes/spain.jpeg'
import uk from './imagenes/uk.png'
import francia from './imagenes/francia.png'
import Contexto from './contexto/Context'

function Idiomas() {
    const {alumno} = useContext(Contexto)
    const {setAlumno} = useContext(Contexto)

    const idioma1 = () =>{
        setAlumno(
            alumno.map((dato,index)=>
            index==3
            ? {...dato,idioma:0}
            : {...dato}
            )
        )
    }
    const idioma2 = () =>{
        setAlumno(
            alumno.map((dato,index)=>
            index==3
            ? {...dato,idioma:1}
            : {...dato}
            )
        )
    }
    const idioma3 = () =>{
        setAlumno(
            alumno.map((dato,index)=>
            index==3
            ? {...dato,idioma:2}
            : {...dato}
            )
        )
    }
    
  return (
    <div className='idiomas'>
        <div className='bandera' onClick={idioma1} value="0"><img src={spain} alt="" /></div>
        <div className='bandera' onClick={idioma2} value="1"><img src={uk} alt="" /></div>
        <div className='bandera' onClick={idioma3} value="2"><img src={francia} alt="" /></div>
    </div>
  )
}

export default Idiomas
