import React, { Children, createContext, useState } from 'react'

export const Contexto = createContext();

export const Datos = ({children}) => {
    const[color,setColor] = useState("silver")
    return(
        <Contexto.Provider value={(color,setColor)}>
            {children}
        </Contexto.Provider>
    )    
}
