import React,{useState,useRef} from 'react'

const App = () => {

  const [texto,setTexto] = useState();
  const [numero,setNumero] = useState(0);
  const [texto1,setTexto1] = useState();
  const [texto2,setTexto2] = useState();
  const reyesGodos=[
    {
      nombre:"Ataulfo",
      aficion: "comer toros sin pelar",
    },{
      nombre:"Recesvinto",
      aficion: "leer a hegel en arameo",
    },{
      nombre:"Teodorico",
      aficion: "la cria del escarabajo en almibar",
    }
  ]

  const ver =()=>{
    setTexto(`La Aficion secreta de ${reyesGodos[numero].nombre} es ${reyesGodos[numero].aficion}`);
    setNumero(numero+1);
    if(numero === reyesGodos.length-1){
      setNumero(0);
    }
    console.log(numero);
    console.log(texto);
  }


  const guardar = ()=>{
    const nuevoRey={
      nombre:texto1,
      aficion:texto2,
    }
    reyesGodos.push(nuevoRey);

  }
  const guardar1 = (e)=>{
    setTexto1(e.target.value);

  }
  const guardar2 = (e)=>{
    setTexto2(e.target.value);

  }
  return (
    <div className='container mt-5'>
      <button onClick={ver} className='btn btn-outline-success'>Ver la siguiente</button>
      <h2>{texto}</h2>
      <input value={texto1} onChange={guardar1}/> Nombre rey
      <input value={texto2} onChange={guardar2}/> Aficcion del rey
      <button onClick={guardar} className='btn btn-outline-success'>Guardar</button>

    </div>
  )
}

export default App
