import React, { useState } from 'react';

const App = () => {
  const [texto, setTexto] = useState('');
  const [numero, setNumero] = useState(0);
  const [texto1, setTexto1] = useState('');
  const [texto2, setTexto2] = useState('');
  const [reyesGodos, setReyesGodos] = useState([
    {
      nombre: "Ataulfo",
      aficion: "comer toros sin pelar",
    },{
      nombre: "Recesvinto",
      aficion: "leer a hegel en arameo",
    },{
      nombre: "Teodorico",
      aficion: "la cria del escarabajo en almibar",
    }
  ]);

  const ver = () => {
    setTexto(`La Afición secreta de ${reyesGodos[numero].nombre} es ${reyesGodos[numero].aficion}`);
    setNumero(numero + 1);
    if (numero === reyesGodos.length - 1) {
      setNumero(0);
    }
  };

  const guardar = () => {
    const nuevoRey = {
      nombre: texto1,
      aficion: texto2,
    };
    setReyesGodos([...reyesGodos, nuevoRey]);
    setTexto1('');
    setTexto2(''); 
  };

  return (
    <div className='container mt-5  text-center'>
      <div className='row '>
        <div className='col-12 d-flex flex-column'> 
          <label> Euro </label>
          <input className='pt-3 pb-3 text-center'/>
        </div>
        <div className='col-12 d-flex flex-column'> 
          <label> Peso Argentino </label>
          <input />
        </div>
        <div className='col-12 d-flex flex-column'> 
          <label> Peso Colombiano </label>
          <input />
        </div>
        <div className='col-12 d-flex flex-column'> 
          <label> Peso Mexicano </label>
          <input />
        </div>
        <div className='col-12 d-flex flex-column'> 
          <label> Dollar </label>
          <input />
        </div>

      </div>
    </div>
  );
};

export default App;
