import React from 'react'
import { useEffect,useState} from 'react';

function App() {
  const [lee,setLee] = useState();


  const [nombre,setNombre] = useState(""); 
  const [imagen,setImg] = useState(""); 

  useEffect(()=>{
    const url="https://randomuser.me/api";
    const peticion=fetch(url);
    peticion.then(datos=>datos.json())
    .then(lectura=>{
      setNombre(lectura.results[0].name.first)
      setImg(lectura.results[0].picture.large)
    })
    .catch(()=>console.log("Error"))
  },[lee])

  const leer = (e) =>{

    setLee(e.target.value);
    console.log(lee);
  }

  return (
    <>
      <div>
        <h1>{nombre}</h1>
        <img src={imagen} alt="" />
        <input type="text" value={lee} onChange={leer}/>
      </div>
    </>


  )
}

export default App
